import React, { Component } from "react";
import Particles from "react-tsparticles";

class ParticleSettings extends Component {
  render() {
    return (
    <div>
      <Particles
        height="1000px"
        width="100vw"
        id="tsparticles"
        options={
          {
            background: {
              color:{
                value:"#0d47a1"
              }
            },
            fpsLimit: 60,
            interactivity:{
              detect_on:'canvas',
              events:{
                onclick: {
                  enable: 'true',
                  mode: 'push'
                },
                onHover: {
                  enable: 'true',
                  mode: 'repulse'
                },
                resize: 'true'
              },
              modes: {
                bubble: {
                  distance: 400,
                  duration: 2,
                  opacity: 0.8,
                  size: 40
                },
                push: {
                  quantity: 4
                },
                repulse: {
                  distance: 200,
                  duration: 0.4
                }
              }
            },
            particles: {
              color: {
                value: "#ffffff"
              },
              links: {
                color: "#ffffff",
                distance: 150,
                enable: true,
                opacity: 0.5,
                width: 1
              },
              collisions: {
                enable: true
              },
              move: {
                direction: "none",
                enable: true,
                outMode: "bounce",
                random: false,
                speed: 3,
                str
              }
            }
          }
        }
      />
    </div>
    );
  }
}

export default ParticleSettings;
