import React, {Component} from 'react';
import Navbar from './Navbar.js';
import Web3 from 'web3';

class App extends Component {

    async Web3() {
        if (window.ethereum) {
            window.web3
        }
    }

    constructor(props) {
        super(props);
        this.state = {
            account: '0x0'
        }
    }

    render() {
        return (
            <div>
                <Navbar account={this.state.account} />
                <div className='text-center'>
                    <p></p>
                </div>
            </div>
        )
    }
}

export default App;
